package day03;

public class ex05for문 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		//for(시작할 수 ; 검사조건 ; 반복을 위한 업무){
		     //실행문장;
		for(i = 1; i <= 3; i++) {
			System.out.println("for문 안의 값 : " + i);
		}
		//반복문 이후 i 값 확인하기
		System.out.println("최종 값 : " + i);

	}

}
